import validatePassword from "./validatePassword"
import validatePhone from "./Val"

function validate(formData) {
    let errors = {}

    // ***phone errors***

    if (!formData.phone) {
        errors.phone = "Phone number is required"
    }
    else if (formData.phone.length !== 10) {
        errors.phone = "Phone number should have 10 digits"
    }
    else if (formData.phone.charAt(0) !== '2' && formData.phone.charAt(0) !== '6') {
        errors.phone = "First digit of the phone number should be 2 or 6"
    }

    // ***email errors***

    if (!formData.email) {
        errors.email = "Email is required"
    }
    else if (!/\S+@\S+\.\S+/.test(formData.email)) {
        errors.email = "Please give a valid email"
    }

    // ***password errors***

    if (!formData.password) {
        errors.password = "Password is required"
    }
    else if (formData.password.length < 9) {
        errors.password = "Password needs to be more than 8 characters"
    }
    else {
        errors.password = validatePassword(formData.password)
    }

    return errors
}

export default validate
