import React, {useState, useEffect} from 'react';
import './App.css';
import Section1 from './Section1'
import Section2 from './Section2';

function App() {
  const [section, setSection] = useState(1)
  const [isLoading, setIsLoading] = useState(true)
  const [homeData, setHomeData] = useState()
  const [menuData, setMenuData] = useState()
  const [sliderData, setSliderData] = useState()
  const [pageData, setPageData] = useState()


  // ena url me diaforetiko endname kathefora `${}`
  const homeUrl = "https://voda-react-assessment.herokuapp.com/home"
  const menuUrl =   "https://voda-react-assessment.herokuapp.com/menu"
  const sliderUrl =   "https://voda-react-assessment.herokuapp.com/slider"
  const pageUrl =   "https://voda-react-assessment.herokuapp.com/page"

  useEffect(()=>{
    let firstAPICall = fetch(homeUrl)
    let secondAPICall = fetch(menuUrl)
    let thirdAPICall = fetch(sliderUrl)
    let fourthAPICall = fetch(pageUrl)

    Promise.all([firstAPICall, secondAPICall,thirdAPICall,fourthAPICall])
    .then(values => Promise.all(values.map(value => value.json())))
    .then(finalVals => {
      setHomeData(finalVals[0])
      setMenuData(finalVals[1])
      setSliderData(finalVals[2])
      set
      setIsLoading(false)
    })
  },[])

  console.log("home",homeData)
  console.log("menu",menuData)
  console.log("slider",sliderData)

  if(isLoading)
  return <h1>Loading</h1>
  else 
  return(
    <div>
      <h1>Slide 1</h1>
      <p>{homeData[0].name}</p>
      <p>text for slide</p>
      <h3>Section 1</h3>
      <button onClick = {()=>setSection(1)}>1</button>
      <h3>Section 2</h3>
      <button onClick = {()=>setSection(2)}>2</button>

      {section === 1? <Section1/>:<Section2/>}
    </div>
  )
}

export default App;
