import React from 'react'
import useForm from './useForm'

export default function Section2(props) {
    const {formData, errorData, handleChange, handleSubmit} = useForm()
    const {services} = props

    return (
        <div className="section2">
            <div className="box slider">Slider is here So be Careful</div>
            <div className="box form">
                <h2 className="validate">{services.formText}</h2>
                <p>We work with ecosystem leaders, corporations and startups worldwide.</p>
                <p>How can we help you?</p>
                <form onSubmit={handleSubmit} className="submitting-form">
                    <input
                     className={`${errorData.phone && "error-border"}`}
                      type="text" placeholder={services.formLabels[0]} 
                      name="phone" value={formData.phone} onChange={handleChange}/>
                    {errorData.phone && <p className="error">{errorData.phone}</p>}
                    <input className={`${errorData.email && "error-border"}`} type="text" placeholder={services.formLabels[1]} name="email" value={formData.email} onChange={handleChange}/>
                    {errorData.email && <p className="error">{errorData.email}</p>}
                    <input  className={`${errorData.password && "error-border"}`}type="password" placeholder={services.formLabels[2]} name="password" value={formData.password} onChange={handleChange}/>  
                    {errorData.password && <p className="error">{errorData.password}</p>}
                    <button>{services.buttonText}</button>
                </form>
            </div>
        </div>
    )
}
