import React from 'react'
import useForm from './useForm'

export default function Section2(props) {
    const {formData, errorData, handleChange, handleSubmit} = useForm()
    const {services} = props

    return (
        <div className="section2">
            <div className="box slider">Slider is here So be Careful</div>
            <div className="box form">
                <h2 className="validate">{services.formText}</h2>
                <p>We work with ecosystem leaders, corporations and startups worldwide.</p>
                <p>How can we help you?</p>
                <form onSubmit={handleSubmit} className="submitting-form">
                    <input type="text" placeholder={services.formLabes} name="phone" value={formData.phone} onChange={handleChange}/>
                    {errorData.phone && <p>{errorData.phone}</p>}
                    <input type="text" placeholder="Your Email" name="email" value={formData.email} onChange={handleChange}/>
                    {errorData.email && <p>{errorData.email}</p>}
                    <input type="password" placeholder="Your Password" name="password" value={formData.password} onChange={handleChange}/>  
                    {errorData.password && <p>{errorData.password}</p>}
                    <button>{services.buttonText}</button>
                </form>
            </div>
        </div>
    )
}
